import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import crud from "../conexiones/crud";
import swal from "sweetalert";

const Login = () => {
  const navigate = useNavigate();

  const [usuario, setUsuario] = useState({
    email: "",
    password: "",
  });

  const { email, password } = usuario;

  const onChange = (e) => {
    setUsuario({
      ...usuario,
      [e.target.name]: e.target.value,
    });
  };
  const autenticarUsuario = async () => {
    const data = {
      email: usuario.email,
      password: usuario.password,
    };

    const response = await crud.POST(`/api/auth`, data);
    const mensaje = response.msg;
    console.log(mensaje);
    if (mensaje === "el usuario no existe") {
      const mensaje = "el usuario no existe";
      swal({
        title: "Error",
        text: mensaje,
        icon: "error",
        buttons: {
          confirm: {
            text: "OK",
            value: true,
            visible: true,
            className: "btn btn-danger",
            closeModal: true,
          },
        },
      });
    } else if (mensaje === "password incorrecto") {
      const mensaje = "password incorrecto";
      swal({
        title: "Error",
        text: mensaje,
        icon: "error",
        buttons: {
          confirm: {
            text: "OK",
            value: true,
            visible: true,
            className: "btn btn-danger",
            closeModal: true,
          },
        },
      });
    } else {
      const jwt = response.token;
      localStorage.setItem("token", jwt);

      // redireccionar a la pantalla de administrador
      navigate("/admin");
    }
  };

  const onSubmbit = (e) => {
    e.preventDefault();
    autenticarUsuario();
  };

  return (
    <main className="container mx-auto mt-5 md:mt-20 p-5 md:flex md:justify-center ">
      <div className="md:w-2/3 lg:w-2/5">
        <h1 className="bg-gradient-to-r bg-slate-900 bg-clip-text font-display text-5xl tracking-wider text-center font-extrabold">
          Iniciar sesión
        </h1>

        <form
          className="my-10 bg-neutral-100 shadow rounded-lg p-10 "
          onSubmit={onSubmbit}
        >
          <div className="my-5">
            <label className="uppercase text-gray-600 block text-xl font-bold">
              Email
            </label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Email de Registro"
              className="w-full mt-3 p-3 border rounded-lg bg-gray-50"
              value={email}
              onChange={onChange}
            />

            <label className="uppercase text-gray-600 block text-xl font-bold">
              password
            </label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Password de Registro"
              className="w-full mt-3 p-3 border rounded-lg bg-gray-50"
              value={password}
              onChange={onChange}
            />
          </div>

          <input
            type="submit"
            value="Iniciar Sesión"
            className="bg-yellow-500 mb-5 w-full py-3 text-white uppercase font-bold rounded hover:cursor-pointer hover:bg-yellow-300 transition-colors"
          />
          <Link
            to={"/crear-cuenta"}
            className="block text-left p-5 mr-5 text-yellow-800 uppercase text-sm font-extrabold"
          >
            Crear Cuenta
          </Link>
          <Link
            to={"/"}
            className="block text-right my-5 text-violet-600 uppercase text-sm"
          >
            Atrás
          </Link>
        </form>
      </div>
    </main>
  );
};

export default Login;
