import React, { useEffect, useState } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import crud from '../conexiones/crud';
import { Link, useNavigate, useParams } from 'react-router-dom';
import swal from 'sweetalert';

const ActualizarProductos = () => {

  const navigate = useNavigate();

  const { idProductos } = useParams();
  console.log(idProductos);

  const [productos, setProductos] = useState({
    nombre: '',
    imagen: ''
  })

  const cargarProductos = async () => {
    const response = await crud.GET(`/api/producto/${idProductos}`);
    setProductos(response.productos);
  }
  useEffect(() => {
    cargarProductos();
  }, []);

  let { nombre, descripcion, stock, precio, imagen } = productos;
  const onChange = (e) => {
    setProductos({
      ...productos,
      [e.target.name]: e.target.value,
      
    })
  }

  const ActualizarProductos = async () => {
    const data = {
      nombre: productos.nombre,
      descripcion: productos.descripcion,
      stock: productos.stock,
      precio: productos.precio,
      imagen: productos.imagen
    }

    const response = await crud.PUT(`/api/productos/${idProductos}`, data);
    console.log(response);
    const mensaje1 = "El producto se actualizó correctamente";

    swal({
      title: 'Información',
      text: mensaje1,
      icon: 'success',
      buttons: {
        confirm: {
          text: 'OK',
          value: true,
          visible: true,
          className: 'btn btn-primary',
          closeModal: true
        }
      }
    });
    navigate("/admin");
  }

  const onSubmit = (e) => {
    e.preventDefault();
    ActualizarProductos();
  }
  return (
    <>
      <Header />
      <div className='md:flex md:min-h-screen'>
        <Sidebar />
        <main className='flex-1'>
          <div className='mt-10 flex justify-center'>
            <h1 className="inline bg-gradient-to-r from-yellow-500 via-yellow-700 to-indigo-500 bg-clip-text font-display text-5xl tracking-tight text-transparent">
              Actualizar Producto
            </h1>
          </div>

          <div className='mt-10 flex justify-center' >
            <form
              className='my-10 bg-white shadow rounded-lg p-10 '
              onSubmit={onSubmit}
            >
              <div className='my-5'>
                <label className='uppercase text-gray-600 block text-xl font-bold' >Nombre del producto</label>
                <input
                  type="text"
                  id="nombre"
                  name="nombre"
                  placeholder='Nombre'
                  className='w-full mt-3 p-3 border rounded-lg bg-gray-50'
                  value={nombre}
                  onChange={onChange}
                />
                <label className='uppercase text-gray-600 block text-xl font-bold' >descripcion del producto</label>
                <input
                  type="text"
                  id="descripcion"
                  name="descripcion"
                  placeholder='Descripcion'
                  className='w-full mt-3 p-3 border rounded-lg bg-gray-50'
                  value={descripcion}
                  onChange={onChange}
                />
                <label className='uppercase text-gray-600 block text-xl font-bold' >stock del producto</label>
                <input
                  type="number"
                  id="stock"
                  name="stock"
                  placeholder='Stock'
                  className='w-full mt-3 p-3 border rounded-lg bg-gray-50'
                  value={stock}
                  onChange={onChange}
                />
                <label className='uppercase text-gray-600 block text-xl font-bold' >precio del producto</label>
                <input
                  type="number"
                  id="precio"
                  name="precio"
                  placeholder='Precio'
                  className='w-full mt-3 p-3 border rounded-lg bg-gray-50'
                  value={precio}
                  onChange={onChange}
                />

                <label className='uppercase text-gray-600 block text-xl font-bold' >Imagen de la categoria</label>
                <input
                  type="text"
                  id="imagen"
                  name="imagen"
                  placeholder='imagen'
                  className='w-full mt-3 p-3 border rounded-lg bg-gray-50'
                  value={imagen}
                  onChange={onChange}
                />
              </div>

              <input
                type="submit"
                value="Actualizar Productos"
                className="bg-yellow-700 mb-5 w-full py-3 text-white uppercase font-bold rounded hover:cursor-pointer hover:bg-yellow-500 transition-colors"
              />

            </form>
          </div >


        </main>
      </div>


    </>
  );
}

export default ActualizarProductos;
